  `trade` varchar(255) DEFAULT NULL COMMENT '交易号',
  `total_fee` decimal(10,2) DEFAULT NULL COMMENT '支付金额',
  `dk` decimal(10,2) DEFAULT NULL COMMENT '抵扣金额',
